const randomFixInt = require('../../lib/random.js');
const randomIntFix = require('../../lib/random.js');

test("При равных значениях, будет то же значение.", () => {

    expect(randomIntFix(1, 1)).toBe(1);
    expect(randomIntFix(-1, -1)).toBe(1);
    expect(randomIntFix(0, 0)).toBe(0);

});

test("При значении типа *строка*, проверяется, является ли значение числом.", () => {

    expect(randomIntFix(0, "100", () => 0.5)).toBe(50);
    expect(() => {
        randomIntFix("1", "n", () => 0.5);
    }).toThrow();
    expect(randomIntFix("50", 100, () => 0.0)).toBe(50);

});

test("Порядок аргументов не важен", () => {

    expect(randomIntFix(3, 1, () => 0.5)).toBe(2);
})

test("При значении типа Infinite будет ошибка", () => {

    expect(() => {
        randomIntFix(,
            ,
            () => 0.5)
    }).toThrow();

});

test("Бот игнорирует отрицательные значения", () => {

    expect(randomIntFix(-10, -50, () => 0.5)).toBe(30);
    expect(randomIntFix(-50, -10, () => 0.5)).toBe(30);
});

test("При пустом значении у функции есть резервные значения (min = 1, max = 100)", () => {

    expect(randomIntFix(undefined, undefined, () => 0.5)).toBe(51);
    expect(randomIntFix(1, undefined, () => 0.5)).toBe(51);
    expect(randomIntFix(undefined, 100, () => 0.5)).toBe(51);

});








